    IN _ReservationId VARCHAR(255),
    IN _Status VARCHAR(255)
)
BEGIN
    DECLARE _GownId VARCHAR(255);

    -- Get the GownId associated with the reservation
    SELECT GownId INTO _GownId FROM reservations WHERE Id = _ReservationId;

    -- Update the reservation status
    UPDATE reservations 
    SET `Status` = _Status
    WHERE Id = _ReservationId;

    -- Update the gown status based on reservation status
    IF _Status = 'Approved' THEN
        UPDATE gowns 
        SET `Status` = 'Reserved' 
        WHERE Id = _GownId;
    ELSEIF _Status = 'Declined' THEN
        UPDATE gowns 
        SET `Status` = 'Available' 
        WHERE Id = _GownId;
    END IF;
END ;;
